const g=n=>{const t=new Date(n),o=t.getDate().toString().padStart(2,"0"),r=(t.getMonth()+1).toString().padStart(2,"0"),e=t.getFullYear(),a=t.getHours().toString().padStart(2,"0"),s=t.getMinutes().toString().padStart(2,"0"),d=t.getSeconds().toString().padStart(2,"0");return`${o}.${r}.${e} ${a}:${s}:${d}`},c=n=>n.toString().replace(/\B(?=(\d{3})+(?!\d))/g," ");export{g as d,c as n};
